import React, { useState } from "react";
import {
  MDBTable,
  MDBBtn,
  MDBTableHead,
  MDBTableBody,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalFooter,
} from "mdb-react-ui-kit";
import { deleteHash } from "./apiServices";

const DataTable = ({ data = [] }) => {
  const [hashData, setHashData] = useState(data);
  const [deleteModal, setDeleteModal] = useState(false);
  const [selectedHash, setSelectedHash] = useState(null);
  const toggleShowDelete = () => setDeleteModal(!deleteModal);

  const handleDelete = async () => {
    try {
      await deleteHash(selectedHash.id);
      hashData.filter((item) => item.id !== selectedHash.hashId); // this line allows to update the table without refreshing the page
    } catch (error) {
      // Handle error (e.g., token expiration or other API issues)
    }
    setHashData(hashData.filter((item) => item.id !== selectedHash.hashId));
    toggleShowDelete();
  };

  return hashData && hashData.length > 0 ? (
    <>
      <div className="d-flex align-items-center justify-content-center mt-5">
        <MDBTable>
          <MDBTableHead>
            <tr>
              <th>Hash Value</th>
              <th>Description</th>
              <th>Actions</th>
            </tr>
          </MDBTableHead>
          <MDBTableBody>
            {hashData.map((item) => (
              <tr key={item.id}>
                <td>{item.hash_value}</td>
                <td>{item.description}</td>
                <td>
                  <div className="btn-group" role="group">
                    <button
                      type="button"
                      className="btn btn-secondary"
                      onClick={() => {
                        console.log("hola");
                      }}
                    >
                      Edit
                    </button>
                    <button
                      type="button"
                      className="btn btn-danger"
                      onClick={() => {
                        setSelectedHash(item);
                        toggleShowDelete();
                      }}
                    >
                      Delete
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </MDBTableBody>
        </MDBTable>
      </div>
      <MDBModal show={deleteModal} setShow={setDeleteModal} tabIndex="-1">
        <MDBModalDialog className="modal-sm text-white">
          <MDBModalContent>
            <MDBModalHeader className="bg-danger">
              <MDBModalTitle>Delete stored hash?</MDBModalTitle>
              <MDBBtn
                className="btn-close"
                color="none"
                onClick={() => {
                  toggleShowDelete();
                }}
              ></MDBBtn>
            </MDBModalHeader>

            <MDBModalFooter className="d-flex justify-content-center">
              <MDBBtn
                className="btn btn-outline-danger"
                color="secondary"
                onClick={() => {
                  toggleShowDelete();
                }}
              >
                Cancel
              </MDBBtn>
              <MDBBtn
                className="btn btn-danger"
                onClick={() => {
                  console.log(selectedHash);
                  handleDelete();
                }}
              >
                Delete
              </MDBBtn>
            </MDBModalFooter>
          </MDBModalContent>
        </MDBModalDialog>
      </MDBModal>
    </>
  ) : (
    <div className="d-flex align-items-center justify-content-center mt-5">
      <h1>No hashes stored!</h1>
    </div>
  );
};

export default DataTable;
