import React, { useState } from "react";
import { useAuth } from "./AuthContext";
import NotLoggedInComponent from "./NotLoggedInComponent";
import { MDBBtn } from "mdb-react-ui-kit";

const Account = () => {
  const { currentUser } = useAuth();
  const [showModalPassword, setShowModalPassword] = useState(false);
  const [showModalAccount, setShowModalAccount] = useState(false);
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const toggleShowPassword = () => {
    setShowModalPassword(!showModalPassword);
  };
  const toggleShowAccount = () => {
    setShowModalAccount(!showModalAccount);
  };

  const handleCurrentPasswordChange = (e) => {
    setCurrentPassword(e.target.value);
  };
  const handleNewPasswordChange = (e) => {
    setNewPassword(e.target.value);
  };

  const handleChangePassword = async () => {
    try {
      //const data = await changePassword();
    } catch (error) {
      console.log(error);
    }
  };

  return currentUser ? (
    <>
      <div className="d-flex align-items-center justify-content-center mt-5">
        <h1>Hello, {currentUser}! What do you want to do?</h1>
      </div>
      <div className="d-flex align-items-center justify-content-center mt-5">
        <MDBBtn onClick={handleChangePassword}>Change password</MDBBtn>
      </div>
      <div className="d-flex align-items-center justify-content-center mt-3">
        <MDBBtn /* onClick={} */>Delete account</MDBBtn>
      </div>
      <MDBModal
        show={showModalPassword}
        setShow={setShowModalPassword}
        tabIndex="-1"
      >
        <MDBModalDialog className="modal-lg">
          <MDBModalContent>
            <MDBModalHeader>
              <MDBModalTitle>
                Please enter the required information
              </MDBModalTitle>
              <MDBBtn
                className="btn-close"
                color="none"
                onClick={() => {
                  toggleShow();
                }}
              ></MDBBtn>
            </MDBModalHeader>
            <MDBModalBody>
              <form
                onSubmit={handleChangePassword}
                className="align-items-center"
              >
                <MDBInput
                  className="mt-3"
                  type="password"
                  id="current_password"
                  name="current_password"
                  label="Current password"
                  maxLength={66} // bytes32 length in hexadecimal
                  pattern="^(?!\d+$).{8,}"
                  value={hashValue} // initial value: the hash value of the selected hash
                  onChange={handleHashChange}
                  required
                />
                <MDBTextArea
                  className="mt-3"
                  id="description"
                  name="description"
                  rows={4}
                  label="Description"
                  value={description} // initial value: the description of the selected hash
                  onChange={handleDescriptionChange}
                  required
                />

                <MDBBtn type="submit" className="mt-4" block>
                  Update
                </MDBBtn>
              </form>
            </MDBModalBody>
          </MDBModalContent>
        </MDBModalDialog>
      </MDBModal>
    </>
  ) : (
    <NotLoggedInComponent />
  );
};

export default Account;
