import React, { useState } from "react";
import {
  MDBTable,
  MDBBtn,
  MDBTableHead,
  MDBTableBody,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalFooter,
  MDBInput,
  MDBModalBody,
} from "mdb-react-ui-kit";
import { deleteHash } from "./apiServices";

const DataTable = ({ data = [] }) => {
  const [hashData, setHashData] = useState(data);
  const [deleteModal, setDeleteModal] = useState(false);
  const [editModal, setEditModal] = useState(false);
  const [selectedHash, setSelectedHash] = useState(null);
  const toggleShowDelete = () => setDeleteModal(!deleteModal);
  const toggleShowEdit = () => setEditModal(!editModal);

  const handleDelete = async () => {
    try {
      await deleteHash(selectedHash.id);
      setHashData(hashData.filter((item) => item.id !== selectedHash.id)); // this line allows to update the table without refreshing the page
    } catch (error) {
      // Handle error (e.g., token expiration or other API issues)
    }
    toggleShowDelete();
  };

  const handleEdit = async (hashId) => {
    try {
      const updatedHash = await editHash(hashId, hashValue, description);
      setHashData((prev) =>
        prev.map((hash) => (hash.id === hashId ? updatedHash : hash))
      );
    } catch (error) {
      // Handle error (e.g., token expiration or other API issues)
    }
  };

  return hashData && hashData.length > 0 ? (
    <>
      <div className="d-flex align-items-center justify-content-center mt-5">
        <MDBTable>
          <MDBTableHead>
            <tr>
              <th>Hash Value</th>
              <th>Description</th>
              <th>Actions</th>
            </tr>
          </MDBTableHead>
          <MDBTableBody>
            {hashData.map((item) => (
              <tr key={item.id}>
                <td>{item.hash_value}</td>
                <td>{item.description}</td>
                <td>
                  <div className="btn-group" role="group">
                    <button
                      type="button"
                      className="btn btn-secondary"
                      onClick={() => {
                        setSelectedHash(item);
                        toggleShowEdit();
                      }}
                    >
                      Edit
                    </button>
                    <button
                      type="button"
                      className="btn btn-danger"
                      onClick={() => {
                        setSelectedHash(item);
                        toggleShowDelete();
                      }}
                    >
                      Delete
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </MDBTableBody>
        </MDBTable>
      </div>
      <MDBModal show={editModal} setShow={setEditModal} tabIndex="-1">
        <MDBModalDialog className="modal-md">
          <MDBModalContent>
            <MDBModalHeader>
              <MDBModalTitle>Please enter the new information</MDBModalTitle>
              <MDBBtn
                className="btn-close"
                color="none"
                onClick={() => {
                  toggleShowEdit();
                }}
              ></MDBBtn>
            </MDBModalHeader>
            <MDBModalBody>
              <form
                onSubmit={editContract}
                className="d-flex align-items-center justify-content-between gap-2"
              >
                <MDBInput
                  type="text"
                  id="form2Example1"
                  label="Stored hash"
                  maxLength={66} // bytes32 length in hexadecimal
                  pattern="^0x[a-fA-F0-9]{64}$" // Ensure it starts with 0x and followed by 64 hexadecimal characters
                  required
                  value={formHash}
                  onChange={handleChangeHash}
                />
              </form>
            </MDBModalBody>
          </MDBModalContent>
        </MDBModalDialog>
      </MDBModal>
      <MDBModal show={deleteModal} setShow={setDeleteModal} tabIndex="-1">
        <MDBModalDialog className="modal-sm text-white">
          <MDBModalContent>
            <MDBModalHeader className="bg-danger">
              <MDBModalTitle>Delete stored hash?</MDBModalTitle>
              <MDBBtn
                className="btn-close"
                color="none"
                onClick={() => {
                  toggleShowDelete();
                }}
              ></MDBBtn>
            </MDBModalHeader>

            <MDBModalFooter className="d-flex justify-content-center">
              <MDBBtn
                className="btn btn-outline-danger"
                color="secondary"
                onClick={() => {
                  toggleShowDelete();
                }}
              >
                Cancel
              </MDBBtn>
              <MDBBtn
                className="btn btn-danger"
                onClick={() => {
                  handleDelete();
                }}
              >
                Delete
              </MDBBtn>
            </MDBModalFooter>
          </MDBModalContent>
        </MDBModalDialog>
      </MDBModal>
    </>
  ) : (
    <div className="d-flex align-items-center justify-content-center mt-5">
      <h1>No hashes stored!</h1>
    </div>
  );
};

export default DataTable;
