import React, { useState } from "react";
import { ethers } from "ethers";
import {
  MDBInput,
  MDBCol,
  MDBRow,
  MDBCheckbox,
  MDBBtn,
  MDBIcon,
} from "mdb-react-ui-kit";
import documentProofABI from "../assets/documentProofABI.json";

const CONTRACT_ADDRESS = "0xb9a219631aed55ebc3d998f17c3840b7ec39c0cc"; // HAY QUE MOVERLO A OTRO SITIO

const Retrieve = () => {
  const [formData, setFormData] = useState({ _hash: "" });

  const handleChange = (e) => {
    setFormData({ ...formData, _hash: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(formData);
    if (typeof window.ethereum === "undefined") {
      alert("Please install MetaMask or another Ethereum wallet extension.");
      return;
    }
    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(
        CONTRACT_ADDRESS,
        documentProofABI,
        signer
      );

      const tx = await contract.newInstance(formData._hash);
      console.log("Transaction hash:", tx.hash);

      await tx.wait();
      console.log("Transaction confirmed.");
    } catch (error) {
      console.error("Error fetching contract data:", error);
      throw error;
    }
  };

  return (
    <div className="d-flex align-items-center justify-content-center mt-5">
      <form onSubmit={handleSubmit}>
        <MDBInput
          className="mb-4"
          type="text"
          id="form2Example1"
          label="Stored hash"
          maxLength={66} // bytes32 length in hexadecimal
          pattern="^0x[a-fA-F0-9]{64}$" // Ensure it starts with 0x and followed by 64 hexadecimal characters
          required
          vlue={formData._hash}
          onChange={handleChange}
        />

        <MDBBtn type="submit" className="mb-4" block>
          Retrieve
        </MDBBtn>
      </form>
    </div>
  );
};

export default Retrieve;
