import React, { useEffect, useState } from "react";
import { useAuth } from "./AuthContext";
import { useNavigate, Link } from "react-router-dom";
import {
  fetchHashes,
  refreshToken,
  deleteHash,
  editHash,
  saveHash,
} from "./apiServices";
import {
  MDBTable,
  MDBTableHead,
  MDBTableBody,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBInput,
} from "mdbreact";

const Dashboard = () => {
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const [hashData, setHashData] = useState(null);
  const [modalInfo, setModalInfo] = useState({ type: "", hash: null });
  const [hashValue, setHashValue] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    if (!currentUser) return;

    const fetchData = async () => {
      try {
        const data = await fetchHashes();
        setHashData(data);
      } catch (error) {
        handleFetchError(error);
      }
    };

    const handleFetchError = async (error) => {
      if (error.response && error.response.status === 401) {
        try {
          const tokenData = await refreshToken();
          localStorage.setItem("access_token", tokenData.access);
          fetchData();
        } catch (refreshError) {
          if (
            refreshError.response &&
            (refreshError.response.status === 401 ||
              refreshError.response.status === 403)
          ) {
            navigate("/login");
          } else {
            alert(
              `Error refreshing token: ${JSON.stringify(
                refreshError.response.data
              )}`
            );
          }
        }
      } else {
        alert(`Error fetching data: ${JSON.stringify(error.response.data)}`);
      }
    };

    fetchData();
  }, [currentUser, navigate]);

  const toggleModal = (type, hash = null) => {
    setModalInfo({ type, hash });
    if (hash) {
      setHashValue(hash.hash_value);
      setDescription(hash.description);
    } else {
      setHashValue("");
      setDescription("");
    }
  };

  const handleDelete = async (hashId) => {
    try {
      await deleteHash(hashId);
      setHashData((prev) => prev.filter((hash) => hash.id !== hashId));
    } catch (error) {
      // Handle error (e.g., token expiration or other API issues)
    }
  };

  const handleEdit = async (hashId) => {
    try {
      const updatedHash = await editHash(hashId, hashValue, description);
      setHashData((prev) =>
        prev.map((hash) => (hash.id === hashId ? updatedHash : hash))
      );
      toggleModal("");
    } catch (error) {
      // Handle error (e.g., token expiration or other API issues)
    }
  };

  const handleSave = async () => {
    try {
      const newHash = await saveHash(hashValue, description);
      setHashData((prev) => [...prev, newHash]);
      toggleModal("");
    } catch (error) {
      // Handle error (e.g., token expiration or other API issues)
    }
  };

  return currentUser ? (
    hashData ? (
      <>
        <MDBBtn
          color="primary"
          className="float-right mb-3"
          onClick={() => toggleModal("save")}
        >
          Save New Hash
        </MDBBtn>
        <DataTable data={hashData} toggleModal={toggleModal} />
        <ModalComponent
          modalInfo={modalInfo}
          toggleModal={toggleModal}
          handleDelete={handleDelete}
          handleEdit={handleEdit}
          handleSave={handleSave}
          hashValue={hashValue}
          setHashValue={setHashValue}
          description={description}
          setDescription={setDescription}
        />
      </>
    ) : (
      <LoadingComponent />
    )
  ) : (
    <NotLoggedInComponent />
  );
};

const NotLoggedInComponent = () => (
  <>
    <div className="d-flex align-items-center justify-content-center mt-5">
      <h1>Not logged in!</h1>
    </div>
    <div className="d-flex align-items-center justify-content-center mt-2">
      <p>
        You need to <Link to="/login">log in</Link> to see this page.
      </p>
    </div>
  </>
);

const LoadingComponent = () => (
  <div className="d-flex align-items-center justify-content-center mt-5">
    <h2>Loading data...</h2>
  </div>
);

const DataTable = ({ data, toggleModal }) => (
  <MDBTable className="mt-5">
    <MDBTableHead>
      <tr>
        <th>ID</th>
        <th>Hash Value</th>
        <th>Description</th>
        <th>Actions</th>
      </tr>
    </MDBTableHead>
    <MDBTableBody>
      {data.map((item) => (
        <tr key={item.id}>
          <td>{item.id}</td>
          <td>{item.hash_value}</td>
          <td>{item.description}</td>
          <td>
            <MDBBtn color="warning" onClick={() => toggleModal("edit", item)}>
              Edit
            </MDBBtn>
            <MDBBtn color="danger" onClick={() => toggleModal("delete", item)}>
              Delete
            </MDBBtn>
          </td>
        </tr>
      ))}
    </MDBTableBody>
  </MDBTable>
);

const ModalComponent = ({
  modalInfo,
  toggleModal,
  handleDelete,
  handleEdit,
  handleSave,
  hashValue,
  setHashValue,
  description,
  setDescription,
}) => {
  return (
    <MDBModal isOpen={modalInfo.type !== ""} toggle={() => toggleModal("")}>
      <MDBModalHeader toggle={() => toggleModal("")}>
        {modalInfo.type === "delete"
          ? "Delete Confirmation"
          : modalInfo.type === "edit"
          ? "Edit Hash"
          : "Save New Hash"}
      </MDBModalHeader>
      <MDBModalBody>
        {modalInfo.type === "delete" ? (
          <p>Are you sure you want to delete this hash?</p>
        ) : (
          <>
            <MDBInput
              label="Hash Value"
              value={hashValue}
              onChange={(e) => setHashValue(e.target.value)}
            />
            <MDBInput
              label="Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </>
        )}
      </MDBModalBody>
      <MDBModalFooter>
        {modalInfo.type === "delete" && (
          <>
            <MDBBtn
              color="danger"
              onClick={() => handleDelete(modalInfo.hash.id)}
            >
              Delete
            </MDBBtn>
            <MDBBtn color="secondary" onClick={() => toggleModal("")}>
              Cancel
            </MDBBtn>
          </>
        )}
        {modalInfo.type === "edit" && (
          <>
            <MDBBtn
              color="warning"
              onClick={() => handleEdit(modalInfo.hash.id)}
            >
              Update
            </MDBBtn>
            <MDBBtn color="secondary" onClick={() => toggleModal("")}>
              Cancel
            </MDBBtn>
          </>
        )}
        {modalInfo.type === "save" && (
          <>
            <MDBBtn color="primary" onClick={handleSave}>
              Save
            </MDBBtn>
            <MDBBtn color="secondary" onClick={() => toggleModal("")}>
              Cancel
            </MDBBtn>
          </>
        )}
      </MDBModalFooter>
    </MDBModal>
  );
};

export default Dashboard;
