import React, { useState } from "react";
import { ethers } from "ethers";

const Upload = () => {
  const [formData, setFormData] = useState({
    _hash: "",
    _size: "",
    _ownerName: "",
    _title: "",
    _description: "",
  });

  const handleChange = (e) => {
    setFormData(e.target.value);
  };

  return (
    <>
      <div className="d-flex align-items-center justify-content-center mt-5">
        <form
          onSubmit={handleSubmit}
          className="col-lg-4 col-md-10 col-sm-10 col-10"
        >
          <MDBInput
            className="mb-4"
            type="text"
            id="_hash"
            label="Hash*"
            maxLength={66} // bytes32 length in hexadecimal
            pattern="^0x[a-fA-F0-9]{64}$" // Ensure it starts with 0x and followed by 64 hexadecimal characters
            required
            value={formData._hash}
            onChange={handleChange}
          />
          <MDBInput
            type="number"
            className="mb-4"
            id="_size"
            name="_size"
            label="Size"
            value={formData._size}
            onChange={handleChange}
          />
          <MDBInput
            type="text"
            className="mb-4"
            id="_title"
            name="_title"
            label="Title"
            value={formData._title}
            onChange={handleChange}
          />

          <MDBBtn type="submit" className="mb-4" block>
            Upload instance
          </MDBBtn>
        </form>
      </div>
    </>
  );
};

export default Upload;
