import React from "react";
import axios from "axios";
import { useAuth, useNavigate } from "./AuthContext";

const Dashboard = () => {
  const navigate = useNavigate();

  const { currentUser } = useAuth(); // Destructure the currentUser and logout object from the context

  const getUserDetailsAndDisplay = () => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8000/api/v1/hashes/",
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("access_token")}`,
            },
          }
        );
        console.log(response.data);
      } catch (error) {
        if (error.response && error.response.status === 401) {
          try {
            const tokenResponse = await axios.post(
              "http://localhost:8000/auth/token/refresh/",
              {
                refresh: localStorage.getItem("refresh_token"),
              }
            );

            localStorage.setItem("access_token", tokenResponse.data.access);
            // After refreshing the token, we can re-fetch the data.
            fetchData();
          } catch (refreshError) {
            if (
              refreshError.response &&
              (refreshError.response.status === 401 ||
                refreshError.response.status === 403)
            ) {
              // Redirect to login if refresh token is expired or not valid.
              navigate("/login");
            } else {
              // For other errors during the refresh process.
              alert(
                `Error refreshing token: ${JSON.stringify(
                  refreshError.response.data
                )}`
              );
            }
          }
        } else {
          // For other errors on the initial request.
          alert(`Error fetching data: ${JSON.stringify(error.response.data)}`);
        }
      }
    };

    // Fetch the data when the component mounts.
    React.useEffect(() => {
      fetchData();
    }, []);

    return <></>;
  };

  return <>{currentUser ? getUserDetailsAndDisplay() : notLoggedIn()}</>;
};

function notLoggedIn() {
  return (
    <>
      <div className="d-flex align-items-center justify-content-center mt-5">
        <h1>Not logged in!</h1>
      </div>
      <div className="d-flex align-items-center justify-content-center mt-2">
        <p>
          You need to <a href="/login">log in</a> to see this page.
        </p>
      </div>
    </>
  );
}

export default Dashboard;
