import React, { useEffect, useState } from "react";
import { useAuth } from "./AuthContext";
import { useNavigate, Link } from "react-router-dom";
import {
  fetchHashes,
  refreshToken,
  deleteHash,
  editHash,
  saveHash,
} from "./apiServices";
import { MDBBtn } from "mdbreact";
import DataTable from "./DataTable";
import ModalComponent from "./ModalComponent";
import LoadingComponent from "./LoadingComponent";
import NotLoggedInComponent from "./NotLoggedInComponent";

const Dashboard = () => {
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const [hashData, setHashData] = useState(null);
  const [modalInfo, setModalInfo] = useState({ type: "", hash: null });
  const [hashValue, setHashValue] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    if (!currentUser) return;

    const fetchData = async () => {
      try {
        const data = await fetchHashes();
        setHashData(data);
      } catch (error) {
        handleFetchError(error);
      }
    };

    const handleFetchError = async (error) => {
      if (error.response && error.response.status === 401) {
        try {
          const tokenData = await refreshToken();
          localStorage.setItem("access_token", tokenData.access);
          fetchData();
        } catch (refreshError) {
          const errorMessage = refreshError.response
            ? JSON.stringify(refreshError.response.data)
            : "An error occurred while refreshing the token.";
          alert(errorMessage);

          if (
            refreshError.response &&
            (refreshError.response.status === 401 ||
              refreshError.response.status === 403)
          ) {
            navigate("/login");
          }
        }
      } else {
        const errorMessage = error.response
          ? JSON.stringify(error.response.data)
          : "An error occurred.";
        alert(`Error fetching data: ${errorMessage}`);
      }
    };

    fetchData();
  }, [currentUser, navigate]);

  const toggleModal = (type, hash = null) => {
    setModalInfo({ type, hash });
    if (hash) {
      setHashValue(hash.hash_value);
      setDescription(hash.description);
    } else {
      setHashValue("");
      setDescription("");
    }
  };

  const handleDelete = async (hashId) => {
    try {
      await deleteHash(hashId);
      setHashData((prev) => prev.filter((hash) => hash.id !== hashId));
    } catch (error) {
      // Handle error (e.g., token expiration or other API issues)
    }
  };

  const handleEdit = async (hashId) => {
    try {
      const updatedHash = await editHash(hashId, hashValue, description);
      setHashData((prev) =>
        prev.map((hash) => (hash.id === hashId ? updatedHash : hash))
      );
      toggleModal("");
    } catch (error) {
      // Handle error (e.g., token expiration or other API issues)
    }
  };

  const handleSave = async () => {
    try {
      const newHash = await saveHash(hashValue, description);
      setHashData((prev) => [...prev, newHash]);
      toggleModal("");
    } catch (error) {
      // Handle error (e.g., token expiration or other API issues)
    }
  };

  return JSON.stringify(hashData);

  /* return currentUser ? (
    hashData ? (
      <>
        <MDBBtn
          color="primary"
          className="float-right mb-3"
          onClick={() => toggleModal("save")}
        >
          Save New Hash
        </MDBBtn>
        <DataTable data={hashData} toggleModal={toggleModal} />
        <ModalComponent
          modalInfo={modalInfo}
          toggleModal={toggleModal}
          handleDelete={handleDelete}
          handleEdit={handleEdit}
          handleSave={handleSave}
          hashValue={hashValue}
          setHashValue={setHashValue}
          description={description}
          setDescription={setDescription}
        />
      </>
    ) : (
      <LoadingComponent />
    )
  ) : (
    <NotLoggedInComponent />
  ); */
};

export default Dashboard;
