import React, { useEffect, useState } from "react";
import { useAuth } from "./AuthContext";
import { useNavigate, Link } from "react-router-dom";
import { fetchHashes, refreshToken, saveHash } from "./apiServices";
import DataTable from "./DataTable";
import LoadingComponent from "./LoadingComponent";
import NotLoggedInComponent from "./NotLoggedInComponent";
import {
  MDBBtn,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBInput,
  MDBModalBody,
  MDBTextArea,
} from "mdb-react-ui-kit";

const Dashboard = () => {
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const [hashData, setHashData] = useState(null);
  const [hashValue, setHashValue] = useState("");
  const [description, setDescription] = useState("");
  const [showModal, setShowModal] = useState(false);
  const toggleShow = () => {
    setShowModal(!showModal);
  };

  const handleHashChange = (e) => {
    setHashValue(e.target.value);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  useEffect(() => {
    if (!currentUser) return;

    const fetchData = async () => {
      try {
        const data = await fetchHashes();
        setHashData(data);
      } catch (error) {
        handleFetchError(error);
      }
    };

    const handleFetchError = async (error) => {
      if (error.response && error.response.status === 401) {
        try {
          const tokenData = await refreshToken();
          localStorage.setItem("access_token", tokenData.access);
          fetchData();
          console.log("Token refreshed.");
        } catch (refreshError) {
          const errorMessage = refreshError.response
            ? JSON.stringify(refreshError.response.data)
            : "An error occurred while refreshing the token.";
          alert(errorMessage);

          if (
            refreshError.response &&
            (refreshError.response.status === 401 ||
              refreshError.response.status === 403)
          ) {
            navigate("/login");
          }
        }
      } else {
        const errorMessage = error.response
          ? JSON.stringify(error.response.data)
          : "An error occurred.";
        alert(`Error fetching data: ${errorMessage}`);
      }
    };

    fetchData();
  }, [currentUser, navigate]);

  const handleSave = async () => {
    e.preventDefault();
    try {
      const newHash = await saveHash(hashValue, description);
      setHashData((prev) => [...prev, newHash]);
    } catch (error) {
      // Handle error (e.g., token expiration or other API issues)
      console.log(error);
    }
    toggleShowEdit();
  };

  return currentUser ? (
    hashData ? (
      <>
        <DataTable data={hashData} />
        <div className="d-flex align-items-center justify-content-center mt-4">
          <MDBBtn onClick={toggleShow}>Save new hash</MDBBtn>
        </div>
        <MDBModal show={showModal} setShow={setShowModal} tabIndex="-1">
          <MDBModalDialog className="modal-lg">
            <MDBModalContent>
              <MDBModalHeader>
                <MDBModalTitle>
                  Please enter the required information
                </MDBModalTitle>
                <MDBBtn
                  className="btn-close"
                  color="none"
                  onClick={() => {
                    toggleShow();
                  }}
                ></MDBBtn>
              </MDBModalHeader>
              <MDBModalBody>
                <form onSubmit={handleSave} className="align-items-center">
                  <MDBInput
                    className="mt-3"
                    type="text"
                    id="hash_value"
                    name="hash_value"
                    label="Hash"
                    maxLength={66} // bytes32 length in hexadecimal
                    pattern="^0x[a-fA-F0-9]{64}$" // Ensure it starts with 0x and followed by 64 hexadecimal characters
                    required
                    value={hashValue} // initial value: the hash value of the selected hash
                    onChange={handleHashChange}
                  />
                  <MDBTextArea
                    className="mt-3"
                    id="description"
                    name="description"
                    rows={4}
                    label="Description"
                    value={description} // initial value: the description of the selected hash
                    onChange={handleDescriptionChange}
                    required
                  />

                  <MDBBtn type="submit" className="mt-4" block>
                    Update
                  </MDBBtn>
                </form>
              </MDBModalBody>
            </MDBModalContent>
          </MDBModalDialog>
        </MDBModal>
      </>
    ) : (
      <LoadingComponent />
    )
  ) : (
    <NotLoggedInComponent />
  );
};

export default Dashboard;
