import React, { useState } from "react";
import { MDBInput, MDBBtn } from "mdb-react-ui-kit";
import axios from "axios";

const Login = () => {
  const { setAuthDetails } = useAuth(); // Destructure the setAuthDetails function from the context
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:8000/auth/token/",
        formData,
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      if (response.data && response.data.access && response.data.refresh) {
        setAuthDetails(formData.username, {
          access: response.data.access,
          refresh: response.data.refresh,
        });

        alert("Login successful!");
        console.log("Login successful!");
        console.log(response);
      }
    } catch (error) {
      // Check if the error response has data (it will, if it's a response error from server)
      if (error.response && error.response.data) {
        alert("Error registering: \n\n" + JSON.stringify(error.response.data));
        console.error("Error registering:", error.response.data);
      } else {
        alert("There was an error: \n\n" + error);
        console.error("There was an error:", error);
      }
    }
  };

  return (
    <div className="d-flex align-items-center justify-content-center mt-5">
      <form
        onSubmit={handleSubmit}
        className="col-lg-3 col-md-5 col-sm-7 col-8"
      >
        <MDBInput
          className="mb-4"
          id="loginUser"
          label="User"
          name="username"
          required
          onChange={handleChange}
        />
        <MDBInput
          className="mb-4"
          type="password"
          id="loginPassword"
          label="Password"
          name="password"
          onChange={handleChange}
          required
        />

        <MDBBtn type="submit" className="mb-4" block>
          Login
        </MDBBtn>

        <div className="text-center">
          Not a member? <a href="/register">Register</a>
        </div>
      </form>
    </div>
  );
};

export default Login;
