import { useState } from "react";
import {
  MDBFile,
  MDBCard,
  MDBCardBody,
  MDBCardHeader,
  MDBCardText,
} from "mdb-react-ui-kit";

const HashGenerator = () => {
  const [hash, setHash] = useState("");

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = async (evt) => {
        const buffer = evt.target.result;
        const digest = await crypto.subtle.digest("SHA-256", buffer);
        const hashArray = Array.from(new Uint8Array(digest));
        const hashHex = hashArray
          .map((b) => b.toString(16).padStart(2, "0"))
          .join("");
        setHash(hashHex);
      };
      reader.readAsArrayBuffer(file);
    }
  };

  return (
    <div className="col-lg-4 col-md-10 col-sm-10 col-10">
      <MDBFile
        label="You can upload a file to generate a hash (we don't store any information)"
        id="customFile"
        onChange={handleFileChange}
      />
      {hash && (
        <div className="mt-3">
          <MDBCard style={{ maxWidth: "45rem" }}>
            <MDBCardHeader color="dark">Generated hash</MDBCardHeader>
            <MDBCardBody>
              <MDBCardText>{"0x" + hash}</MDBCardText>
            </MDBCardBody>
          </MDBCard>
        </div>
      )}
    </div>
  );
};

export default HashGenerator;
