import React, { useEffect, useState } from "react";
import { useAuth } from "./AuthContext";
import { useNavigate, Link } from "react-router-dom";
import { fetchHashes, refreshToken } from "./apiServices";

const Dashboard = () => {
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const [hashData, setHashData] = useState(null);

  useEffect(() => {
    if (!currentUser) return;

    const fetchData = async () => {
      try {
        const data = await fetchHashes();
        setHashData(data);
      } catch (error) {
        handleFetchError(error);
      }
    };

    const handleFetchError = async (error) => {
      if (error.response && error.response.status === 401) {
        try {
          const tokenData = await refreshToken();
          localStorage.setItem("access_token", tokenData.access);
          fetchData();
        } catch (refreshError) {
          if (
            refreshError.response &&
            (refreshError.response.status === 401 ||
              refreshError.response.status === 403)
          ) {
            navigate("/login");
          } else {
            alert(
              `Error refreshing token: ${JSON.stringify(
                refreshError.response.data
              )}`
            );
          }
        }
      } else {
        alert(`Error fetching data: ${JSON.stringify(error.response.data)}`);
      }
    };

    fetchData();
  }, [currentUser, navigate]);

  return currentUser ? (
    hashData ? (
      <DataTable data={hashData} />
    ) : (
      <LoadingComponent />
    )
  ) : (
    <NotLoggedInComponent />
  );
};

const NotLoggedInComponent = () => (
  <>
    <div className="d-flex align-items-center justify-content-center mt-5">
      <h1>Not logged in!</h1>
    </div>
    <div className="d-flex align-items-center justify-content-center mt-2">
      <p>
        You need to <Link to="/login">log in</Link> to see this page.
      </p>
    </div>
  </>
);

const LoadingComponent = () => (
  <div className="d-flex align-items-center justify-content-center mt-5">
    <h2>Loading data...</h2>
  </div>
);

const DataTable = ({ data }) => (
  <table className="table mt-5">
    <thead>
      <tr>
        <th>ID</th>
        <th>Hash Value</th>
        <th>Description</th>
        <th>User</th>
      </tr>
    </thead>
    <tbody>
      {data.map((item) => (
        <tr key={item.id}>
          <td>{item.id}</td>
          <td>{item.hash_value}</td>
          <td>{item.description}</td>
          <td>{item.user}</td>
        </tr>
      ))}
    </tbody>
  </table>
);

export default Dashboard;
